document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".header-burger"),t=document.querySelector(".header__main-menu");e.addEventListener("click",()=>{e.classList.toggle("icon-active"),t.classList.toggle("menu-active")}),t.addEventListener("click",()=>{"header__main-menu menu-active"==t.classList.value&&(e.classList.remove("icon-active"),t.classList.remove("menu-active"))})});